import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const statusMap = {
  pending: { label: 'Sipariş Alındı', color: 'bg-yellow-400 text-yellow-900' },
  preparing: { label: 'Hazırlanıyor', color: 'bg-blue-500 text-white' },
  ready: { label: 'Hazır', color: 'bg-green-500 text-white' },
};

const TrackOrderPage = () => {
  const { orderId } = useParams();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const res = await axios.get(`http://localhost:8000/order/${orderId}`);
        setOrder(res.data);
      } catch (err) {
        setOrder(null);
      } finally {
        setLoading(false);
      }
    };
    fetchOrder();
    const interval = setInterval(fetchOrder, 4000);
    return () => clearInterval(interval);
  }, [orderId]);

  if (loading) {
    return <div className="min-h-screen flex items-center justify-center">Yükleniyor...</div>;
  }
  if (!order) {
    return <div className="min-h-screen flex items-center justify-center text-red-600">Sipariş bulunamadı.</div>;
  }

  // Toplam fiyatı hesapla
  const total = order.items.reduce((sum, item) => sum + (item.price || 0) * item.quantity, 0);

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-blue-50 flex flex-col items-center py-12">
      <div className="bg-white/95 rounded-3xl shadow-2xl p-8 w-full max-w-2xl border border-gray-200">
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-3xl font-extrabold text-blue-900 drop-shadow-sm">Sipariş Takibi</h1>
          <span className={`px-5 py-2 rounded-full text-lg font-bold shadow ${statusMap[order.status]?.color || 'bg-gray-300 text-gray-700'}`}>{statusMap[order.status]?.label || order.status}</span>
        </div>
        <div className="mb-4 flex gap-8">
          <p className="text-gray-700 text-lg">Sipariş No: <span className="font-semibold">{order.id}</span></p>
          <p className="text-gray-700 text-lg">Masa: <span className="font-semibold">{order.table_id}</span></p>
        </div>
        <div className="mb-8">
          <h2 className="text-xl font-bold mb-2 text-blue-900">Sipariş Detayları</h2>
          <div className="overflow-x-auto">
            <table className="w-full text-left border-separate border-spacing-y-2">
              <thead>
                <tr>
                  <th className="py-2 px-2 bg-orange-50 rounded-l text-gray-700">Ürün</th>
                  <th className="py-2 px-2 bg-orange-50 rounded-r text-gray-700">Adet</th>
                </tr>
              </thead>
              <tbody>
                {order.items.map(item => (
                  <tr key={item.id} className="bg-gray-50">
                    <td className="py-2 px-2 rounded-l font-medium text-gray-900">{item.menu_item_name}</td>
                    <td className="py-2 px-2 rounded-r text-center text-orange-600 font-bold">{item.quantity}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        <div className="flex items-center justify-between pt-4 border-t-2 border-gray-100">
          <span className="text-lg font-semibold text-gray-700">Toplam Tutar:</span>
          <span className="text-2xl font-extrabold text-orange-500">{total.toFixed(2)}₺</span>
        </div>
      </div>
    </div>
  );
};

export default TrackOrderPage;
